import { Component } from '@angular/core';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'firstapp';
  name = 'shashank';
   isDisabled = false;
   color = 'Red';
   value = "abcd";
  getName(): string {
    return this.name;


  }
  addValue() {
    alert(this.value);
  }
}
