import { Component } from '@angular/core';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  // isDisabled = false;

  // color = 'black';



  name = "";

   surname = "";

   Gender :string = 'Male';


//   user: User = {
//     name: "nitin",
//     surname: "Me"
//   };


  submit(): void {
    console.log(`Customer Name is : ${this.name} ${this.surname}  and his Gender is :${this.Gender}`);

    setTimeout(() => {
      alert(`Submited with: ${this.name} ${this.surname}`);
    },1000);

  }





store(name:string){
   if(name==='shashank'){
    alert("Hey There")
   }
}

}





// export interface User {
// name: string;
// surname: string;
// }

<label for="validationCustom01" class="form-label">First name<sup style="color: red;">*</sup></label> 

  <input type="text" required  name="name"  #box  placeholder="First Name"  (click)="store(name)" [(ngModel)]="name"><br>


  <label for="validationCustom01" class="form-label">Last name<sup style="color: red;">*</sup></label>

  <input type="text" #box1 placeholder="surname" name="surname" [(ngModel)]="surname"/><br>


  <label for="validationCustom01" class="form-label">Email<sup style="color: red;">*</sup></label> 
  <input type="email" class ="new" placeholder="email" /><br>

  <label for="validationCustom01" class="form-label">Birth-Date<sup style="color: red;">*</sup></label>
  <input type="date" class ="new1" placeholder="date"><br><br>

  <label for="validationCustom01" class="form-label">Gender<sup style="color: red;">*</sup></label> 
  <select type = 'text' name="Gender"[(ngModel)]="Gender">

    <option value="Male">Male</option>
    <option value="Female">Female<option>
    <option value="Other">Other</option>
    </select><p>You have selected : <b>{{Gender}}</b></p>

    <label for="exampleFormControlFile1">Resume<sup style="color: red;">*</sup></label>
    <input type="file" class="form-control-file" id="exampleFormControlFile1"><br><br>


  <button type="button" class="btn btn-light"  #box2 (click)="submit()">Submit </button>
