import { Component } from '@angular/core';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 42;
  value = 45 + 25;
  email = "shashankcvn@gmail.com";
  isDisabled = false;

  color = 'black';
  name = "shashank";
  count = 0;
  isSpecial = "";
  age = "26";

  show = "no";

  getName(): number {
    return this.value;


  }
  addValue() {
    alert(this.name);
  }
  submitvalue() {
    alert("You have successfully Submited....!!")
  }
  commented() {
    alert("successfully commited");

  }
  getcounter(type: string) {
    type == 'add' ? this.count++ : this.count--;
  }

  save() {
    alert("saved")
  }

  users = ['shashank', 'neha', 'rush'];
  usersDetails = [{
    name: "shashank", age: 25, email: "shashankcvn@gmail.com"},
   { name: "neha", age: 26, email: "neha04@gmail.com"},
   { name: "rush", age: 24, email: "rush45sh@gmail.com"}
]


emp = [{
  name : "shashank",phoneno : 7869,emp_id : 5609, post: "front-end developer",social_accounts:["skype",'insta','facebook']},
{
  name : "Ram",phoneno : 8769,emp_id : 5611, post: "database developer",social_accounts:["hotmail",'insta','yahoo']
},
{
  name : "veera",phoneno : 7869,emp_id : 5623, post: "Business Analyst",social_accounts:["gmail",'insta','rediff']
}]


updatecolor(){
  this.color='blue';
  alert("Ohho Wow color is updATED...!!")
}

}







