import { Component } from '@angular/core';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 42;
  value = 45 + 25;
  email = "shashankcvn@gmail.com";
  isDisabled = false;

  color = 'black';
  name = "shashank";
  count = 0;
  isSpecial = "";
  age = "26";

  show = "no";

  getName(): number {
    return this.value;


  }
  addValue() {
    alert(this.name);
  }
  submitvalue() {
    alert("You have successfully Submited....!!")
  }
  commented() {
    alert("successfully commited");

  }
  getcounter(type: string) {
    type == 'add' ? this.count++ : this.count--;
  }

  save() {
    alert("saved")
  }

  users = ['shashank', 'neha', 'rush'];
  usersDetails = [{
    name: "shashank", age: 25, email: "shashankcvn@gmail.com"},
   { name: "neha", age: 26, email: "neha04@gmail.com"},
   { name: "rush", age: 24, email: "rush45sh@gmail.com"}
]


emp = [{
  name : "shashank",phoneno : 7869,emp_id : 5609, post: "front-end developer",social_accounts:["skype",'insta','facebook']},
{
  name : "Ram",phoneno : 8769,emp_id : 5611, post: "database developer",social_accounts:["hotmail",'insta','yahoo']
},
{
  name : "veera",phoneno : 7869,emp_id : 5623, post: "Business Analyst",social_accounts:["gmail",'insta','rediff']
}]


updatecolor(){
  this.color='blue';
  alert("Ohho Wow color is updATED...!!")
}

}







<nav class="navbar navbar-expand-lg bg-body-tertiary" data-bs-theme="dark">
  <div class="container-fluid">
    <a class="navbar-brand" href="#">Navbar</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent"
      aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="#">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">Link</a>
        </li>
        <li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            Dropdown
          </a>
          <ul class="dropdown-menu">
            <li><a class="dropdown-item" href="#">Action</a></li>
            <li><a class="dropdown-item" href="#">Another action</a></li>
            <li>
              <hr class="dropdown-divider">
            </li>
            <li><a class="dropdown-item" href="#">Something else here</a></li>
          </ul>
        </li>
        <li class="nav-item">
          <a class="nav-link disabled" aria-disabled="true">Disabled</a>
        </li>
      </ul>
      <form class="d-flex" role="search">
        <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search">
        <button class="btn btn-outline-success" type="submit">Search</button>
      </form>
    </div>

  </div>
</nav>
<h1>{{title}}</h1>
<h2>{{email}}</h2>
<h3>{{getName()}}</h3>


<app-hike></app-hike>

<button [disabled]="isDisabled" type="button" class="btn btn-dark">Dark</button><br><br> -->


<button [style.color]="color" [disabled]="isDisabled" type="button" style="background-color: blue;" >Dark</button>

<input type="text" name="name" [(ngModel)]="name" style=" width: 30%;">
<h5>You Entered :{{name}}</h5>
<br>

<button (click)="addValue()">add</button><br><br>

<button type="button" [style.color]="isSpecial ? 'red' : 'green'" (click)="submitvalue()">submit</button><br><br>

