<button (click)="getcounter('minus')">--</button> -->




 <input type="text" name="name" value="{{name}}" disabled="{{isDisabled}}"><br><br>

<input [disabled]="isDisabled" type="number" age="user_age" [(ngModel)]="age" style="width:25%;"> 


<button (click)="save()" style="margin: 3px; background-color: rgba(0, 255, 255, 0.822); border: 2px solid red;">Save</button><br>
<b>your age is : {{age}}</b><br>

<p *ngIf= "show=='GoAT'; then ifblock else elseblock"></p>

<ng-template #ifblock>
<b>I got it ...!!</b>
</ng-template>

<ng-template #elseblock>
  <b>nah...</b>
</ng-template>

<h6 *ngFor="let item of usersDetails">{{item.name}} -- {{item.age}} -- {{item.email}}</h6> -->


<ul *ngFor="let item of emp" [style.color]="color">

<li>Name : {{item.name}}</li>
<li>Mob : {{item.phoneno}}</li>
<li>Emp_id : {{item.emp_id}}</li>


<ul *ngFor="let account of item.social_accounts">
  <li>{{account}}</li>
</ul>
</ul><br>

<button (click)="updatecolor()" >Update</button>


 <b [style.color]="color">{{email}}</b><br>
