  Gender :["male","female"]
};

mobregex  = /^([+]\d{2}[ ])?\d{10}$/;

clicked(){
  console.log(`${this.user.name}  ${this.user.surname} ${this.user.email} ${this.user.mobile} ${this.user.Age} ${this.user.BirthDate} ${this.user.Gender}`);

