import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

// Login Form

user :  User = {
  name : "shashank",
  surname : "",
  email : "",
  mobile : "",
  Age : 25,
  BirthDate : "",
  Gender :["male","female"]
};

mobregex  = /^([+]\d{2}[ ])?\d{10}$/;

clicked(){
  console.log(`${this.user.name}  ${this.user.surname} ${this.user.email} ${this.user.mobile} ${this.user.Age} ${this.user.BirthDate} ${this.user.Gender}`);

}

// employee information

employee : Employee =[{emp_id :101,name:"shashank",email:"shashankcvn@gmail.com"},
                      {emp_id: 102,name:"Rushi",email:"rushi@gmail.com"},
                      {emp_id :103,name:"kaustubh",email:"kausti41@gmail.com"}]


}


export interface User {
name: string;
surname: string; 
email: string;
mobile : string;
Age : number;
BirthDate : string;
Gender : string[]
}


export interface Employee{
  emp_id : number[];
  name : string [];
  email: string []
}








