import { Component } from '@angular/core';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 42;
  value = 45 + 25;
  email = "shashankcvn@gmail.com";
  isDisabled = false;

  color = 'Red';
  name = "shashank";
  count = 0;
  isSpecial = "";
  age = "26";

  show ="GoAT";

  getName(): number {
    return this.value;


  }
  addValue() {
    alert(this.name);
  }
  submitvalue() {
    alert("You have successfully Submited....!!")
  }
  commented() {
    alert("successfully commited");

  }
  getcounter(type: string) {
    type == 'add' ? this.count++ : this.count--;
  }

  save() {
    alert("saved")
  }

}
