import { Component } from '@angular/core';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 42;
  value = 45 + 25;
  email = "shashankcvn@gmail.com";
  isDisabled = false;

  color = 'black';
  name = "";
  count = 0;
  isSpecial = "";
  age = "26";

  show = "no";
  abcd: string | undefined;

  getName(): number {
    return this.value;


  }
  addValue() {
    alert(this.name);
  }
  submitvalue() {
    alert("You have successfully Submited....!!")
  }
  commented() {
    alert("successfully commited");

  }
  getcounter(type: string) {
    type == 'add' ? this.count++ : this.count--;
  }

  save() {
    alert("saved")
  }

  users = ['shashank', 'neha', 'rush'];
  usersDetails = [{
    name: "shashank", age: 25, email: "shashankcvn@gmail.com"},
   { name: "neha", age: 26, email: "neha04@gmail.com"},
   { name: "rush", age: 24, email: "rush45sh@gmail.com"}
]


emp = [{
