import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-hike',
  templateUrl: './hike.component.html',
  styleUrls: ['./hike.component.css']
})
export class HikeComponent {
     user =[
      {
        employee_no : 101,
        employee_name : "shashank"
      }
     ]



  @Input() item = 0;

 @Output () updateEvent = new EventEmitter<string>(); 

@Output() updateOld = new EventEmitter<any>();



}
 <ul *ngFor="let users of user">
    <li>{{users.employee_no}}</li>
    <li>{{users.employee_name}}</li>
</ul>

<h4>{{item}}</h4> 


<p> 
    Child Component

  </p>


  <label for="validationCustom01" class="form-label">First name<sup style="color: red;">*</sup></label>
  <input type="text" name="data1"  #box  placeholder="User Name"><br>
  <label for="validationCustom01" class="form-label">Age<sup style="color: red;">*</sup></label>
  <input type="number" #box1 placeholder="Age"/><br>
  <select class="form-select" id="validationCustom04" required style="width: 30%;"></select>
 <button (click)="updateEvent.emit(box.value)" (click)="updateOld.emit(box1.value)" >Send</button>
